def question1

    #Déterminer le nombre de handles contenu dans le tableau
    puts "\nQuestionn° 1 : Combien y a-t-il de handle dans cette array ?"

    reponseByCount = $journalists_handles.count
    reponseByLength = $journalists_handles.length

    puts " > Le Array contient #{reponseByCount} handles (méthode count)."
    puts " > Le Array contient #{reponseByLength} handles (méthode length)."



end


def question2

    #Déterminer la handle le plus court du tableau
    puts "\nQuestion n°2 : Quel est le handle le plus court de cette liste ?"
    
    reponse = $journalists_handles.min_by {|size| size.length}
    puts "Le handle le plus court de cette liste est #{reponse}."
end


def question3
    #Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère) ?
    puts "\nQuestion n°3 : Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère) ?"

    reponse =  $journalists_handles.select {|handle| handle.length == 6}.count
    puts "Il y a #{reponse} handle contenant 5 caractères (@ exclus)."
end



def question4
    #Combien commencent par une majuscule (première lettre juste après le @) ?
    puts "\nQuestion n°4 : Combien commencent par une majuscule (première lettre juste après le @) ?"

    reponse = $journalists_handles.select {|handle| handle.split(//)[1] == handle.split(//)[1].upcase}.count 
    puts "#{reponse} handle commencent par une majuscule."
end

def question5
    #Trie la liste de handle par ordre alphabétique.
    puts "\nQuestion n°5 : Trie la liste de handle par ordre alphabétique."
    print "[ENTRÉE POUR LANCER LE TRI]"
    gets
    puts $journalists_handles.sort
end

def question6    
    #Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après).
    puts "\nQuestion n°6 : Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)."
    
    print "[ENTRÉE POUR LANCER LE TRI]"
    gets    
    puts $journalists_handles.sort_by {|handle| handle.length} 
     
end

def question7
    #Quelle est la position dans l'array de la personne @epenser ?
    puts "\nQuestion n°7 : Quelle est la position dans l'array de la personne @epenser ?"

    reponse = $journalists_handles.index("@epenser")
    puts " > \"@epenser\" est à l'index #{reponse}."
end


def question8
    #Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
    puts "\nQuestion n°8 : Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)."

    #Il nous faut d'abord connaître et stocker toutes la taille des handle (dans un tableau allHandlesSizes)
    allHandlesSizes = Array.new

    $journalists_handles.each do |handle|
        allHandlesSizes.push(handle.size)
    end

    #Création d'un tableau qui contient toutes les tailles mais SANS DOUBLONS, et classé dans l'ordre croissant pour faciliter la lecture
    allHandlesSizes_uniq = allHandlesSizes.uniq.sort

    #Affichage de la répartition des tailles de handles.
    for i in 0..allHandlesSizes_uniq.length-1
        reponse = allHandlesSizes.count(allHandlesSizes_uniq[i])
        puts " > Il y a #{reponse} handle de taille #{allHandlesSizes_uniq[i]}."
    end

end

def nextQuestion
    #Permet de faire une pause dans le défilement des questions
    print "\n[ENTRÉE] POUR LA QUESTION SUIVANTE"
    gets
end

